import Headers from "../Headers/Headers";
import MainContent from "../MainContent/MainContent";
import Axios from "axios";
import { useEffect, useState } from "react";
import Prices from "../Prices/Prices";
import SearchBar from "../SearchBar/SearchBar";
function CoinsData() {
  let i = 1;
  const link =
    "https://api.coingecko.com/api/v3/coins/markets?vs_currency=INR&order=market_cap_desc&per_page=100&page=1&sparkline=false";
  const [coins, setCoins] = useState([]);
  useEffect(() => {
    Axios.get(link)
      .then((res) => {
        const data = res.data;
        setCoins(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const rendCoin = coins.map((data) => {
    return (
      <MainContent
        index={i++}
        key={data.id}
        img={data.image}
        name={data.name}
        symbol={data.symbol}
        current_price={data.current_price}
        price_1h={data.price_change_percentage_24h / 24}
        price_24h={data.price_change_percentage_24h}
        price_7days={data.ath_change_percentage}
        total_volume={data.total_volume}
        mkt_cap={data.market_cap}
        mkt_cap_24h={data.market_cap_change_24h}
      />
    );
  });

  function findSums(){
    let values = [];
    let mktCap = []
    coins.map((data) => {
      values.push(data.total_volume);
      mktCap.push(data.market_cap)
    });
    let valueSum = 0;
    for (let i = 0; i < values.length; i++) {
      valueSum += values[i];
    }
    console.log(valueSum);
    return(
      <Prices coinCount={coins.length} valueSum = {valueSum}/>
    )

  }


  return (
    <div>
      {findSums}
      <SearchBar />
      <Headers />
      {rendCoin}
    </div>
  );
}
export default CoinsData;
