import "./MainContentStyles/MainContent.scss";
import star from "./MainContentStyles/star.png";
import { Bar } from "react-chartjs-2";
// eslint-disable-next-line no-unused-vars
import Chart from "chart.js/auto";
function MainContent(props) {
  return (
    <div className="mainContent">
      <div className="index">
        <p>{props.index}</p>
        <img src={star} alt="" width="10%" height="100%" />
      </div>
      <div className="name">
        <img src={props.data.image} alt="" />
        <p>{props.data.name}</p>
      </div>
      <div className="symbol">{props.data.symbol}</div>
      <div className="current_price">${props.data.current_price}</div>
      <div
        className="hourPrice"
        style={{
          color:
            Math.round((props.data.price_change_percentage_24h / 24) * 10) /
              10 >=
            0
              ? "#92C63B"
              : "#DC6969",
        }}
      >
        {Math.round((props.data.price_change_percentage_24h / 24) * 10) / 10}
      </div>
      <div
        className="hour_24Price"
        style={{
          color:
            Math.round(props.data.price_change_percentage_24h * 10) / 10 >= 0
              ? "#92C63B"
              : "#DC6969",
        }}
      >
        {Math.round(props.data.price_change_percentage_24h * 10) / 10}%
      </div>
      <div
        className="sevenDayPrice"
        style={{
          color:
            Math.round(props.data.ath_change_percentage * 10) / 10 >= 0
              ? "#92C63B"
              : "#DC6969",
        }}
      >
        {Math.round(props.data.ath_change_percentage * 10) / 10}%
      </div>
      <div className="volume">${props.data.total_volume}</div>
      <div className="mktCap">${props.data.market_cap}</div>
      <div className="chart">
        <Bar
          data={{
            labels: ["min", "total", "max"],
            datasets: [
              {
                data: [
                  props.data.circulating_supply,
                  props.data.total_supply,
                  props.data.max_supply,
                ],
                backgroundColor: ["#92C63B", "#4CAF4F", "#027F5E"],
                borderColor: ["green"],
              },
            ],
          }}
          height="50%"
          width="50%"
          options={{
            plugins: {
              legend: {
                labels: {
                  font: {
                    size: 3,
                  },
                },
                display: false,
              },
            },
            maintainAspectRatio: false,
          }}
        />
      </div>
    </div>
  );
}

export default MainContent;
