import Headers from "../Headers/Headers";
import MainContent from "../MainContent/MainContent";
import Axios from "axios";
import { useEffect, useState } from "react";
import Prices from "../Prices/Prices";
import SearchBar from "../SearchBar/SearchBar";
function CoinsData() {
  let ind = 1;
  const link =
    "https://api.coingecko.com/api/v3/coins/markets?vs_currency=INR&order=market_cap_desc&per_page=100&page=1&sparkline=false";
  const [coins, setCoins] = useState([]);
  useEffect(() => {
    Axios.get(link)
      .then((res) => {
        const data = res.data;
        setCoins(data);
      })
      .catch((err) => console.log(err));
  }, []);

  function getValueForFilters(searchByName, minPriceValue, maxPriceValue) {
    const rendCoin = coins
    .filter((data)=> {
        if( getValueForFilters.searchByName === ""){
          return data
        }else if(data.name.toLowerCase().includes(getValueForFilters.searchByName.toLowerCase)){
          return data
        }
    })
    .map((data) => {
      return <MainContent index={ind++} key={data.id} data={data} />;
    });
  }





  let values = [];
  let mktCap = [];
  // eslint-disable-next-line array-callback-return
  coins.map((data) => {
    values.push(data.total_volume);
    mktCap.push(data.market_cap);
  });
  let valueSum = 0;
  let mktCapSum = 0;
  for (let i = 0; i < values.length; i++) {
    valueSum += values[i];
  }
  for (let i = 0; i < mktCap.length; i++) {
    mktCapSum += mktCap[i];
  }

  return (
    <div>
      <Prices
        coinCount={coins.length}
        valueSum={valueSum}
        mktCapSum={mktCapSum}
      />
      <SearchBar getValueForFilters={getValueForFilters} />
      <Headers />
      {rendCoin}
    </div>
  );
}
export default CoinsData;
